--
-- Table structure for table `jobs`
--

DROP TABLE IF EXISTS `jobs`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `jobs` (
  `id` int NOT NULL AUTO_INCREMENT,
  `company_id` int NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_general_ci NOT NULL,
  `description` text COLLATE utf8mb4_general_ci NOT NULL,
  `min_salary` bigint NOT NULL,
  `max_salary` bigint NOT NULL,
  `recruitment_number` int NOT NULL,
  `working_experience` int DEFAULT '0',
  `working_method` enum('offline','remote','hybrid') COLLATE utf8mb4_general_ci DEFAULT 'offline',
  `working_type` enum('fulltime','partime') COLLATE utf8mb4_general_ci NOT NULL DEFAULT 'fulltime',
  `expired_date` datetime DEFAULT NULL,
  `start_week_day` int DEFAULT '2',
  `end_week_day` int DEFAULT '6',
  `degree` enum('Thực tập sinh','Nhân viên','Trưởng nhóm','Giám đốc','Tổng giám đốc') COLLATE utf8mb4_general_ci DEFAULT 'Nhân viên',
  `gender` enum('male','female') COLLATE utf8mb4_general_ci DEFAULT NULL,
  `province_id` int NOT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `company_id` (`company_id`),
  KEY `province_id` (`province_id`),
  CONSTRAINT `jobs_ibfk_1` FOREIGN KEY (`company_id`) REFERENCES `companies` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `jobs_ibfk_2` FOREIGN KEY (`province_id`) REFERENCES `provinces` (`id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=172 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

